create database login;
use login;

create table ropa (
id int primary key,
titulo varchar(50) ,
precio float(50),
genero varchar(50),
descripcion varchar(500),
cantidad int,
foto varchar(50)
);

create table carrito(
id int primary key,
titulo varchar(50) ,
precio float(50),
cantidad int,
foto varchar(50)
);

create table copias( 
id int primary key,
cantidad int );


create table usuarios (
usuario varchar(50),
contrasena varchar(50),
nombre varchar(50) ,
direccion varchar(50)
);

create table mensaje (
correo varchar(50),
nombre varchar(50),
apellido varchar(50),
telefono varchar(50),
direccion varchar(50),
observacion varchar(50)
);

create table admins(
usuario varchar(50),
contrasena varchar(50)
);







